import React, { useState } from 'react'

const App = () => {
  const [ persons, setPersons ] = useState([
    {   name: 'Huynh Vi Ha',
        id: 1,
    }
  ])
  const [ newName, setNewName ] = useState('')


  const [ phoneNumbers, setPhoneNumber ] = useState([
    {   phone: '0766901516',
        id: 1,
    }
  ])
  const [ newPhoneNumber, setNewPhoneNumber ] = useState('')


  const addPerson = (event) => {
      event.preventDefault()
      const personObject = {
          name: newName,
          id: persons.length + 1,
      }
      const personName = persons.map(person => person.name)
      const pos = personName.indexOf(newName)
      console.log(pos)
      if (pos === -1)
          setPersons(persons.concat(personObject))
      else
          window.alert(`${newName} is already added to phonebook`)
      setNewName('')
    }

   const addPhoneNumber = (event) => {
      event.preventDefault()
      const phoneNumberObject = {
          phone: newPhoneNumber,
          id: phoneNumbers.length + 1,
      }
      const pos = phoneNumbers.map(phoneNumber => phoneNumber.phone).indexOf(newName)
      console.log(pos)
      if (pos === -1)
          setPersons(phoneNumbers.concat(phoneNumberObject))
      else
          window.alert(`${newPhoneNumber} is already added to phonebook`)
      setNewPhoneNumber('')
    }

  const handlePersonChange = (event) => {
      console.log(event.target.value)
      setNewName(event.target.value)
  }

  const handlePhoneNumberChange = (event) => {
      console.log(event.target.value)
      setNewName(event.target.value)
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit={addPerson}>
        <div>
          name: <input value={newName} onChange={handlePersonChange}/>
        </div>

        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <form onSubmit={addPhoneNumber}>
        <div>
          name: <input value={newPhoneNumber} onChange={handlePhoneNumberChange}/>
        </div>

        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
       <div>
        <ul>
            {persons.map(person => <li key = {person.id}>{person.name}</li>)}
        </ul>
       </div>
    </div>
  )
}

export default App
