import React from 'react'

const PersonForm = ({ props }) => {

    const addPerson = (event) => {
      event.preventDefault()
      const personObject = {
          name: props.newName,
          number: props.newNumber,
          id: persons.length + 1,
      }
      const personName = props.persons.map(person => person.name)
      const pos = personName.indexOf(props.newName)
      console.log(pos)
      if (pos === -1) {
          props.setPersons(props.persons.concat(personObject))
      }
      else
          window.alert(`${props.newName} is already added to phonebook`)
      props.setNewName('')
      props.setNewPhone('')
    }


    const handlePersonChange = (event) => {
      console.log(event.target.value)
      props.setNewName(event.target.value)
    }

    const handlePhoneChange = (event) => {
      console.log(event.target.value)
      props.setNewPhone(event.target.value)
    }

    return(
        <form onSubmit={addPerson}>
        <div>
          name: <input value={props.newName} onChange={handlePersonChange}/>
        </div>
        <div>
          number: <input value={props.newNumber} onChange={handlePhoneChange}/>
        </div>

        <div>
          <button type="submit">add</button>
        </div>
      </form>
    )
}

export default PersonForm
