import React from "react";

const Form = ({ props }) => {
    const handleSearchName = (event) => {
      console.log(event.target.value)
      props.setSearchName(event.target.value)
  }
    return(
            <form onChange={handleSearchName}>
              <div>
                  filter shown with: <input value={props.searchName}/>
              </div>
            </form>
    )
}

export default Form
